'use client';
import Link from 'next/link';
import React, { useState, useEffect } from 'react';
import { useRouter } from 'next/navigation';
import { useDispatch, useSelector } from 'react-redux';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { FiPlus, FiMinus } from 'react-icons/fi';
import { removeFromCart, updateQuantity, setCart } from '../../../store/cartSlice';
import Image from 'next/image';

const CartPage = () => {
  const [total, setTotal] = useState(0);
  const [subtotal, setSubtotal] = useState(0);
  const [deliveryCharge, setDeliveryCharge] = useState(0);

  const cart = useSelector((state) => state.cart.items);
  const dispatch = useDispatch();
  const router = useRouter();
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    const username = localStorage.getItem('authToken');
    setIsAuthenticated(!!username);
  }, [router]);

  const fetchDeliveryCharge = async () => {
    try {
      const response = await fetch('/api/settings/getSetting');
      const data = await response.json();
      setDeliveryCharge(data.deliveryCharge || 0);
    } catch (error) {
      console.error('Error fetching delivery charge:', error);
      setDeliveryCharge(0);
    }
  };

  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem('cart')) || [];
    dispatch(setCart(storedCart));
    fetchDeliveryCharge();
    calculateTotal(storedCart);
  }, [dispatch]);

  useEffect(() => {
    if (cart.length > 0) {
      localStorage.setItem('cart', JSON.stringify(cart));
      calculateTotal(cart);
    }
  }, [cart, deliveryCharge]);

  const calculateTotal = (cartItems) => {
    const calculatedSubtotal = cartItems.reduce((acc, item) => acc + item.price * (item.quantity || 1), 0);
    setSubtotal(calculatedSubtotal);
    setTotal(calculatedSubtotal + deliveryCharge);
  };

  const updateItemQuantity = (itemId, quantity) => {
    if (quantity < 1) return; // Prevent quantity from going below 1
    dispatch(updateQuantity({ id: itemId, quantity }));
    const updatedCart = cart.map((item) =>
      item.id === itemId ? { ...item, quantity } : item
    );
    calculateTotal(updatedCart);
  };

  const handleRemoveFromCart = (itemId) => {
    dispatch(removeFromCart({ id: itemId }));
    const updatedCart = cart.filter((item) => item.id !== itemId);
    localStorage.setItem('cart', JSON.stringify(updatedCart));
    calculateTotal(updatedCart);
    toast.success('Product removed from cart!');
  };

  const handleCheckout = () => {
    router.push(`/customer/pages/checkout?total=${total}`);
  };

  if (cart.length === 0) {
    return (
      <div className="flex flex-col items-center justify-center min-h-screen text-center bg-gray-50 px-4">
        <div className="w-24 h-24 mb-4 sm:w-32 sm:h-32">
          <Image
            width={1000}
            height={1000}
            placeholder="data:image/jpeg;base64,"
            src="/cart.png"
            alt="Empty Cart"
            className="w-full h-full object-contain"
          />
        </div>
        <p className="text-xl sm:text-2xl font-bold text-gray-800">
          Your Cart is <span className="text-red-600">Empty!</span>
        </p>
        <button
          className="mt-6 bg-red-500 hover:bg-red-600 text-white font-semibold py-2 px-6 rounded-md shadow-md"
          onClick={() => router.push('/')}
        >
          RETURN TO SHOP
        </button>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-8 bg-white text-black">
      <div className="flex flex-col lg:flex-row gap-6 lg:gap-8">
        {/* Left Section - Cart Items & Shipping Information */}
        <div className="w-full lg:w-3/5 border border-gray-400 rounded-lg overflow-x-auto">
          {/* Shipping Information Section */}
          <div className="bg-blue-50 p-4 mb-6">
            <div className="flex items-center space-x-4">
              <Image
                width={50}
                height={50}
                placeholder="data:image/jpeg;base64,"
                src="/shipping-icon.png"
                alt="Shipping Icon"
                className="w-12 h-12 sm:w-14 sm:h-14"
              />
              <div>
                <p className="text-base sm:text-lg font-semibold text-gray-800">
                  Shipping, arrives within 5 Days
                </p>
                <p className="text-sm sm:text-md text-gray-600"></p>
              </div>
            </div>
          </div>

          {/* Cart Products */}
          <div className="flex flex-col gap-4 p-4">
            {cart.map((item, index) => (
              <div
                key={index}
                className="grid grid-cols-[100px_1fr_80px] sm:grid-cols-[120px_1fr_100px] items-start border-b border-gray-300 py-4 gap-2 sm:gap-4"
              >
                {/* First Column: Image (100px on Mobile, 120px on Desktop) */}
                <div className="flex justify-center sm:justify-start">
                  {item.images && item.images.length > 0 ? (
                    <Image
                      width={100}
                      height={100}
                      placeholder="data:image/jpeg;base64,"
                      src={`${process.env.NEXT_PUBLIC_UPLOADED_IMAGE_URL}/${item.images[0].url}`}
                      alt={item.name}
                      className="w-[100px] h-[100px] sm:w-[120px] sm:h-[120px] object-cover rounded"
                    />
                  ) : (
                    <div className="w-[100px] h-[100px] sm:w-[120px] sm:h-[120px] bg-gray-200 rounded flex items-center justify-center text-gray-500">
                      No Image
                    </div>
                  )}
                </div>

                {/* Second Column: Title, Price, Quantity, and Remove (Wider) */}
                <div className="flex flex-col gap-1 min-w-0">
                  <h3 className="text-sm sm:text-base text-gray-600 truncate">{item.name.toUpperCase()}</h3>
                  <p className="text-sm sm:text-md font-bold text-black">
                    Rs.{item.price.toLocaleString()}
                  </p>
                  <div className="flex items-center border border-gray-300 rounded-full px-2 py-1 w-fit">
                    <button
                      className="text-gray-700 px-1 sm:px-2"
                      onClick={() => updateItemQuantity(item.id, item.quantity - 1)}
                      disabled={item.quantity <= 1}
                    >
                      <FiMinus />
                    </button>
                    <span className="mx-2 sm:mx-4 text-sm sm:text-base">{item.quantity}</span>
                    <button
                      className="text-gray-700 px-1 sm:px-2"
                      onClick={() => updateItemQuantity(item.id, item.quantity + 1)}
                    >
                      <FiPlus />
                    </button>
                  </div>
                  {/* Show Size and Color on Larger Screens Only */}
                  <div className="hidden sm:block">
                    {item.selectedSize && (
                      <p className="text-sm text-gray-600">Size: {item.selectedSize || 'N/A'}</p>
                    )}
                    {item.selectedColor && (
                      <p className="text-sm text-gray-600">Color: {item.selectedColor || 'N/A'}</p>
                    )}
                    <button
                      className="underline text-sm text-gray-700 text-left mt-1"
                      onClick={() => handleRemoveFromCart(item.id)}
                    >
                      Remove
                    </button>
                  </div>
                </div>

                {/* Third Column: Total Price */}
                <div className="flex justify-end sm:justify-start">
                  <p className="text-sm sm:text-md font-bold text-black text-right sm:text-left whitespace-nowrap">
                    Rs.{(item.quantity * item.price).toLocaleString()}
                  </p>
                </div>

                {/* Additional Details for Mobile (Below the Grid) */}
                <div className="col-span-3 sm:hidden mt-2">
                  {item.selectedSize && (
                    <p className="text-sm text-gray-600">Size: {item.selectedSize || 'N/A'}</p>
                  )}
                  {item.selectedColor && (
                    <p className="text-sm text-gray-600">Color: {item.selectedColor || 'N/A'}</p>
                  )}
                  <button
                    className="underline text-sm text-gray-700 text-left"
                    onClick={() => handleRemoveFromCart(item.id)}
                  >
                    Remove
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Right Section - Order Summary */}
        <div className="w-full lg:w-2/5 p-4">
          <div className="bg-white shadow-lg rounded-lg p-4 sm:p-6 flex flex-col gap-4">
            <div className="flex flex-col sm:flex-row gap-2">
              <a
                href="/"
                className="bg-green-500 text-white py-2 px-4 rounded-full text-sm sm:text-md font-semibold text-center"
              >
                Continue Shopping
              </a>
              <button
                className="bg-blue-500 text-white py-2 px-4 rounded-full text-sm sm:text-md font-semibold"
                onClick={handleCheckout}
              >
                Continue to Checkout
              </button>
            </div>
            {!isAuthenticated && (
              <p className="text-center text-gray-500 text-sm">
                For the best shopping experience,{' '}
                <Link href="/customer/pages/register" className="text-blue-500 underline">
                  sign in
                </Link>
              </p>
            )}
            <hr className="my-4" />
            <div className="text-center mb-4">
              <p className="text-sm sm:text-md font-semibold text-gray-700">
                Taxes and Shipping calculated at checkout.
              </p>
            </div>
            <div className="flex justify-between">
              <p className="text-sm sm:text-md font-semibold text-gray-700">Subtotal:</p>
              <p className="text-sm sm:text-md text-gray-700">
                Rs.{Math.round(subtotal).toLocaleString()}
              </p>
            </div>
            <div className="flex justify-between">
              <p className="text-sm sm:text-md font-semibold text-gray-700">Total:</p>
              <p className="text-sm sm:text-md font-bold text-black">
                Rs.{Math.round(total).toLocaleString()}
              </p>
            </div>
          </div>
        </div>
      </div>
      <ToastContainer />
    </div>
  );
};

export default CartPage;